import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import './App.css';
import RegisterComponent from './RegisterComponent';
import LoginComponent from './LoginComponent';
import AddComponent from './AddComponent';
import EditComponent from './EditComponent';
import DeleteComponent from './DeleteComponent';
import ListComponent from './ListingComponent';


function App() {
  const [username, setUsername] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [songs, setSongs] = useState([]);

  const handleSongAddition = (newSong) => {
    setSongs(prevSongs => [...prevSongs, newSong]);
  };

  const handleSongEdit = (editedSong) => {
    setSongs(prevSongs => prevSongs.map(song => song.id === editedSong.id ? editedSong : song));
  };

  const handleSongDelete = (deletedSongId) => {
    setSongs(prevSongs => prevSongs.filter(song => song.id !== deletedSongId));
  };

  if (!isAuthenticated) {
    return (
      <div className="App">
        <LoginComponent onLogin={() => setIsAuthenticated(true)} />
        <RegisterComponent
          onRegister={(user) => {
            setUsername(user);
            setIsAuthenticated(true);
          }}
          onError={setErrorMessage}
        />
        {errorMessage && <p className="error-message">{errorMessage}</p>}
      </div>
    );
  }

  return (
    <div className="App container">
      <header className="App-header mt-4">
        <h1>SongStars</h1>
        <p>Welcome, {username}!</p>
      </header>

      <Router>
        <Switch>
          <Route path="/edit/:songId" component={EditSong} />
          <Route path="/delete/:songId" component={DeleteSong} />
        </Switch>
      </Router>

      <ListComponent songs={songs} loggedInUser={username} />
      <AddComponent onSongAddition={handleSongAddition} />
    </div>
  );
}

export default App;
