import React, { useEffect, useState } from "react";

const ListComponent = ({ onUpdate, onDelete, loggedInUsername }) => {
  const [isLoading, setLoading] = useState(true);
  const [songs, setSongs] = useState([]);

  useEffect(() => {
    // Function to fetch songs
    const fetchSongs = async () => {
      try {
        const response = await fetch("http://localhost/index.php/song/list");
        const json = await response.json();
        setSongs(json.songs);
      } catch (error) {
        console.error(error);
      } finally {
        setLoading(false);
      }
    };

    // Call the function to fetch songs
    fetchSongs();

    // Subscribe to updates (e.g., add or delete) using WebSocket or other mechanisms
    // This is a simplified example; you may need to implement a more robust solution
    // For example, using WebSocket to receive real-time updates from the server
    const interval = setInterval(fetchSongs, 5000); // Update every 5 seconds

    // Cleanup interval on component unmount
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ padding: "24px" }}>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h2 style={{ fontSize: "18px", color: "green", textAlign: "center" }}>
            Song List
          </h2>
          <p
            style={{
              fontSize: "14px",
              color: "green",
              textAlign: "center",
              paddingBottom: "10px",
            }}
          >
            Songs:
          </p>
          <ul>
            {songs.map((item) => (
              <li key={item.id} style={{ marginBottom: "10px" }}>
                <p>ID: {item.id}</p>
                <p>Title: {item.title}</p>
                <p>Artist: {item.artist}</p>
                <p>Rating: {item.rating}</p>

                {/* Conditionally render Update and Delete buttons */}
                {loggedInUsername === item.user && (
                  <>
                    <button onClick={() => onUpdate(item)}>Update</button>
                    <button onClick={() => onDelete(item.id)}>Delete</button>
                  </>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ListComponent;
