import React, { useEffect, useState } from "react";
import EditComponent from './EditComponent';
import DeleteComponent from './DeleteComponent';

export default function ListComponent({ songs, loggedInUsername }) {
  const [isLoading, setLoading] = useState(true);
  const [showEdit, setShowEdit] = useState(false);
  const [editSong, setEditSong] = useState(null);
  const [showDelete, setShowDelete] = useState(false);
  const [deleteSong, setDeleteSong] = useState(null);

  useEffect(() => {
    setLoading(false);
  }, [songs]);

  const handleEditClick = (song) => {
    // Check if the logged-in user is the same as the user who added the song
    if (loggedInUsername === song.user) {
      // Set the song to be edited and show the EditComponent
      setEditSong(song);
      setShowEdit(true);
    } else {
      console.log("You don't have permission to edit this song.");
    }
  };

  const handleDeleteClick = (song) => {
    // Check if the logged-in user is the same as the user who added the song
    if (loggedInUsername === song.user) {
      // Set the song to be deleted and show the DeleteComponent
      setDeleteSong(song);
      setShowDelete(true);
    } else {
      console.log("You don't have permission to delete this song.");
    }
  };

  const handleEditClose = () => {
    // Close the EditComponent
    setShowEdit(false);
  };

  const handleDeleteClose = () => {
    // Close the DeleteComponent
    setShowDelete(false);
  };

  return (
    <div style={{ padding: "24px" }}>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h2 style={{ fontSize: "18px", color: "green", textAlign: "center" }}>
            Song List
          </h2>
          <p
            style={{
              fontSize: "14px",
              color: "green",
              textAlign: "center",
              paddingBottom: "10px",
            }}
          >
            Songs:
          </p>
          <ul>
            {songs.map((song) => (
              <li key={song.id} style={{ marginBottom: "10px" }}>
                <p>ID: {song.id}</p>
                <p>Title: {song.title}</p>
                <p>Artist: {song.artist}</p>
                <p>Rating: {song.rating}</p>
                {loggedInUsername === song.user && (
                  <>
                    <button onClick={() => handleEditClick(song)}>Edit</button>
                    <button onClick={() => handleDeleteClick(song)}>Delete</button>
                  </>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* EditComponent */}
      {showEdit && <EditComponent song={editSong} onClose={handleEditClose} />}

      {/* DeleteComponent */}
      {showDelete && <DeleteComponent song={deleteSong} onClose={handleDeleteClose} />}
    </div>
  );
}
