import React, { useState } from 'react';
import './App.css';
import RegisterComponent from './RegisterComponent';
import LoginComponent from './LoginComponent';
import AddComponent from './AddComponent';
import EditComponent from './EditComponent';
import DeleteComponent from './DeleteComponent';

function App() {
  const [username, setUsername] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [errorMessage, setErrorMessage] = useState('');
  const [songs, setSongs] = useState([]);

  const handleSongAddition = (newSong) => {
    setSongs((prevSongs) => [...prevSongs, newSong]);
  };

  const handleSongEdit = (editedSong) => {
    setSongs((prevSongs) =>
      prevSongs.map((song) => (song.id === editedSong.id ? editedSong : song))
    );
  };

  const handleSongDelete = (deletedSongId) => {
    setSongs((prevSongs) =>
      prevSongs.filter((song) => song.id !== deletedSongId)
    );
  };

  const login = (user) => {
    setUsername(user);
    setIsAuthenticated(true);
  };

  const register = (user) => {
    setUsername(user);
    setIsAuthenticated(true);
  };

  return (
    <div className="App container">
      <header className="App-header mt-4">
        <h1>SongStars</h1>
        {isAuthenticated && <p>Welcome, {username}!</p>}
      </header>

      {isAuthenticated ? (
        <>
          <AddComponent onSongAddition={handleSongAddition} />
          <EditComponent onSongEdit={handleSongEdit} />
          <DeleteComponent onSongDelete={handleSongDelete} />

          <ul>
            {songs.map((song) => (
              <li key={song.id}>
                {song.title} by {song.artist}
              </li>
            ))}
          </ul>
        </>
      ) : (
        <div className="App">
          <LoginComponent onLogin={login} />
          <RegisterComponent onRegister={register} onError={setErrorMessage} />
          {errorMessage && <p className="error-message">{errorMessage}</p>}
        </div>
      )}
    </div>
  );
}

export default App;
